class ResponseSearchBuku {
  ResponseSearchBuku({
      this.status, 
      this.message, 
      this.data,});

  ResponseSearchBuku.fromJson(dynamic json) {
    status = json['Status'];
    message = json['Message'];
    if (json['data'] != null) {
      data = [];
      json['data'].forEach((v) {
        data?.add(DataSearch.fromJson(v));
      });
    }
  }
  int? status;
  String? message;
  List<DataSearch>? data;

  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
